timestamp=$(echo $GIT_AUTHOR_DATE | awk '{print $1}' | tr -d "@");detil=$((timestamp%86400));diff_seconds=$((timestamp - 1681056000));echo $diff_seconds;days_diff=$((diff_seconds / 86400 * 45 / 68+150));diff_seconds=$(($days_diff*86400+28800));echo $diff_seconds;timestamp=$((1681056000+diff_seconds+detil));result=$(date -d "@$timestamp" +"%Y-%m-%d %H:%M:%S");export GIT_AUTHOR_DATE=$result; export GIT_COMMITTER_DATE=$result;